package com.virtusa.sample.repositoryimport org.springframework.data.jpa.repository.JpaRepositoryimport org.springframework.data.jpa.repository.Queryimport org.springframework.data.repository.query.Paramimport org.springframework.data.rest.core.annotation.RestResourceimport org.springframework.stereotype.Repositoryimport com.virtusa.sample.domain.Patient@Repository@RestResourceinterface PatientRepository extends JpaRepository<Patient, Long> {		@Query("select p from Patient p join fetch p.address")	List<Patient> findAllWithAddress()		@Query("select p from Patient p join fetch p.address where p.id=:id")	Patient findOneWithAddress(@Param("id") id)}
